void ADC12_Config(void)
{
	if (CardType == BF)
	{
		// Select port 6.[0,1,2,3,4,5,6,7] as ADC Peripheral module function
		P6SEL |= (BIT0 + BIT2 + BIT4 + BIT6);		//BF
		P7SEL |= (BIT4 /*+ BIT5*/);					//Tensión DC/DCs en P7.4

		//Configure ADC12 control registers
		ADC12CTL0 |= ADC12SHT1_4/*4*/ + ADC12SHT0_4/*4*/ + ADC12MSC + ADC12ON;  			// SnH MEM8-MEM15 64 cyles, SnH MEM0-MEM7 64 cycles, multiple sample and conversion, ADC12 ON
	    ADC12CTL1 |= ADC12SHP + ADC12DIV_7/*7*/ + ADC12SSEL_2 + ADC12CONSEQ_3;			// SnH pulse mode, Clk divider 8, clk source MCLK, conversion Repeat-sequence-of-channels
	    ADC12CTL2 |= ADC12RES_2 + ADC12TCOFF;											//12-bit resolution
	    REFCTL0 &= ~REFMSTR;															//Desabilitar el módulo REF
	    ADC12CTL0 |= ADC12REF2_5V + ADC12REFON;											//Referencia 2.5V

	    //Assign Conversion Memory Control and Input channels
	    ADC12MCTL0 = ADC12INCH_0;											//Input Channel A0 = Power Previo 1 BF
	    ADC12MCTL2 = ADC12INCH_2;											//Input Channel A2 = Incident Power HPA1 BF
	    ADC12MCTL4 = ADC12INCH_4;											//Input Channel A4 = Reflected Power HPA1 BF
	    ADC12MCTL6 = ADC12INCH_6;											//Input Channel A6 = Temperature HPA1 BF
	    ADC12MCTL12 = ADC12INCH_12 + ADC12EOS;								//Input Channel A12 = Voltage HPA BF/AF

	    //Enables the interrupt request for the ADC12IFG13 bit
	    ADC12IE |= ADC12IE12;

	    ADC12MCTL0 |= ADC12SREF_1;
	    ADC12MCTL2 |= ADC12SREF_1;
	    ADC12MCTL4 |= ADC12SREF_1;
	    ADC12MCTL6 |= ADC12SREF_1;
	    ADC12MCTL12 |= ADC12SREF_1;

	    // Start Conversion
	    ADC12CTL0 |= ADC12ENC + ADC12SC;									//ADC12 Enable, Start Conversion
	}

	if (CardType == AF)
	{
		// Select port 6.[0,1,2,3,4,5,6,7] as ADC Peripheral module function
		P6SEL |= (BIT1 + BIT3 + BIT5 + BIT7);		//AF
		P7SEL |= (BIT4 /*+ BIT5*/);					////BF and AF share DCDC voltage pin

		//Configure ADC12 control registers
		ADC12CTL0 |= ADC12SHT1_4 + ADC12SHT0_4 + ADC12MSC + ADC12ON;  			// SnH MEM8-MEM15 64 cyles, SnH MEM0-MEM7 64 cycles, multiple sample and conversion, ADC12 ON
		ADC12CTL1 |= ADC12SHP + ADC12DIV_7 + ADC12SSEL_2 + ADC12CONSEQ_3;		// SnH pulse mode, Clk divider 8, clk source MCLK, conversion Repeat-sequence-of-channels
		ADC12CTL2 |= ADC12RES_2 + ADC12TCOFF;									//12-bit resolution
		REFCTL0 &= ~REFMSTR;													//Desabilitar el módulo REF
		ADC12CTL0 |= ADC12REF2_5V + ADC12REFON;									//Referencia 2.5V

		//Assign Conversion Memory Control and Input channels
		ADC12MCTL1 = ADC12INCH_1;											//Input Channel A1 = Power Previo 2 AF
		ADC12MCTL3 = ADC12INCH_3;											//Input Channel A3 = Incident Power HPA2 AF
		ADC12MCTL5 = ADC12INCH_5;											//Input Channel A5 = Reflected Power HPA2 AF
		ADC12MCTL7 = ADC12INCH_7;											//Input Channel A7 = Temperature HPA2 AF
		ADC12MCTL12 = ADC12INCH_12 + ADC12EOS;								//Input Channel A12 = Voltage HPA AF/BF

		//Enables the interrupt request for the ADC12IFG13 bit
		ADC12IE |= ADC12IE12 /*BIT7*/;

		ADC12MCTL1 |= ADC12SREF_1;
		ADC12MCTL3 |= ADC12SREF_1;
		ADC12MCTL5 |= ADC12SREF_1;
		ADC12MCTL7 |= ADC12SREF_1;
		ADC12MCTL12 |= ADC12SREF_1;

		// Start Conversion
		ADC12CTL0 |= ADC12ENC + ADC12SC;									//ADC12 Enable, Start Conversion
	}
}



