//#pragma vector=ADC12_VECTOR				//Los vectores de interrupción se pueden consultar en pag.19 del manual del micro MSP430F5636
//__interrupt void ADC12_ISR (void)
void ADC12_ISR(UArg arg0)
{
	//LED_1_POUT |= LED_1_MASK;			//Toggle Pin 9.2 (Sparta & Platinum)

	if (CardType == BF)
	{
		//Power Previo 1 BF - J5
		PrePower_FWD_BF = ADC12MEM0;
		if(PrePower_FWD_BF > 2200)
			PrePower_FWD_BF = 2200;

		//Incident Power HPA1 BF (20Mhz - 500Mhz) - J1
		Power_FWD_BF = ADC12MEM2;
		if(Power_FWD_BF > 4000)
			Power_FWD_BF = 4000;

		//Reflected Power HPA1 BF - J2
		Power_RVS_BF = ADC12MEM4;
		if(Power_RVS_BF > 4000)
			Power_RVS_BF = 4000;

		//OJO! HAY CHANCE DE HACERLE OVERFLOW A ESTAS VARIABLES EN CASO DE MUCHAS MUESTRAS Y POTENCIAS ALTAS
		//Sum values (Tablas en uW !)
		PrePowerSum_FWD_BF += Tabla_dBm2uW_PrevioBF[PrePower_FWD_BF]/*PrePower_FWD_BF*/;
		PowerSum_FWD_BF += Tabla_dBm2uW_BF[Power_FWD_BF];
		PowerSum_RVS_BF += Tabla_dBm2uW_BF[Power_RVS_BF];

		TempeSum_BF += ADC12MEM6;
		VoltageSum_BF += ADC12MEM12;	//PSU Voltage

		//Read
//		Mem1_init = ADC12MEM1;
//		Mem3_init = ADC12MEM3;
//		Mem5_init = ADC12MEM5;
//		Mem7_init = ADC12MEM7;
//		Mem13_init = ADC12MEM13;
	}


	if (CardType == AF)
	{
		//Power Previo 2 AF - J6
		PrePower_FWD_AF = ADC12MEM1;
		if(PrePower_FWD_AF > 2200)
			PrePower_FWD_AF = 2200;

		//Incident Power HPA2 AF (0.5Ghz - 2.7Ghz) - J3
		Power_FWD_AF = ADC12MEM3;
		if(Power_FWD_AF > 4000)
			Power_FWD_AF = 4000;

		//Reflected Power HPA2 AF - J4
		Power_RVS_AF = ADC12MEM5;
		if(Power_RVS_AF > 4000)
			Power_RVS_AF = 4000;

		//OJO! HAY CHANCE DE HACERLE OVERFLOW A ESTAS VARIABLES EN CASO DE MUCHAS MUESTRAS Y POTENCIAS ALTAS
		//Sum values (Tablas en uW !)
		PrePowerSum_FWD_AF += Tabla_dBm2uW_PrevioAF[PrePower_FWD_AF];
		PowerSum_FWD_AF += Tabla_dBm2uW_AF[Power_FWD_AF];
		PowerSum_RVS_AF += Tabla_dBm2uW_AF[Power_RVS_AF];

		TempeSum_AF += ADC12MEM7;
		VoltageSum_AF += ADC12MEM12;	//PSU Voltage -> Same pin for BF and AF

//		Mem0_init = ADC12MEM0;
//		Mem2_init = ADC12MEM2;
//		Mem4_init = ADC12MEM4;
//		Mem6_init = ADC12MEM6;
//		Mem12_init = ADC12MEM12;
	}


	SamplesTaken++;


	if (SamplesTaken >= AD_SAMPLES_PRUEBA /*2^6*/)
	{
		//AUX_2_POUT |= AUX_2_MASK; //Toogle pin 9.7

		//Reset samples taken counter
		SamplesTaken = 0;

		if (CardType == BF)
		{
			//--------------------------------------------------------------
			//Get AVG power values: divide the Power summed values by AD_SAMPLES_PRUEBA or move 12 bits right (todo: parametrizar ese 12)
			PrePowerAvg_FWD_BF = PrePowerSum_FWD_BF / AD_SAMPLES_PRUEBA /*32*/ /*>> AD_SAMPLES_PRUEBA_EXP*/;		//En uW !
			PowerAvg_FWD_BF = PowerSum_FWD_BF / AD_SAMPLES_PRUEBA /*32*/ /*>> AD_SAMPLES_PRUEBA_EXP*/;			//En uW !
			PowerAvg_RVS_BF = PowerSum_RVS_BF / AD_SAMPLES_PRUEBA /*32*/ /*>> AD_SAMPLES_PRUEBA_EXP*/;			//En uW !

			//AVG Values for Temperature
			TempeAvg_BF = TempeSum_BF >> 4;			//2^12 escalones del ADC12 y 2^6 del num de muestras (AD_SAMPLES_PRUEBA)
			TempeAvg_BF *= AD_VREF_PLUS_STEP_mV;
			TempeAvg_BF = TempeAvg_BF >> 14;

			//AVG values for Voltage
			VoltageAvg_BF = VoltageSum_BF >> 4;			//2^12 escalones del ADC12 y 2^6 del num de muestras
			VoltageAvg_BF *= AD_VREF_PLUS_STEP_mV;
			VoltageAvg_BF = VoltageAvg_BF >> 14;

			//Power Previo 1 BF (Previo BF) - J5
			if(PrePowerSum_FWD_BF != 0)
			{
				//2^12 escalones del ADC12 y 2^6 del num de muestras (AD_SAMPLES_PRUEBA)
				//PrePowerAvg_FWD_BF = PrePowerSum_FWD_BF >> 4;
				//PrePowerAvg_FWD_BF *= AD_VREF_PLUS_STEP_mV;
				//PrePowerAvg_FWD_BF = PrePowerAvg_FWD_BF >> 14;

				//Usando recta de ajuste P.Previo BF SN-140002 100Mhz -> y = 0,0312x - 23,22
				//ArrayTControl[REG_POT1PREVIO_BF].s_16 = 10 * ((0.0312 * PrePowerAvg_FWD_BF) - 23.22);	//In 10*dBm
				//ArrayTControl[REG_POT1PREVIO_BF].s_16 = (unsigned long)(((639 * PrePowerAvg_FWD_BF) >> 7) - 3715) >> 4;

				ArrayTControl[REG_POT1PREVIO_BF].s_16 = ADC12_Convert_uW2dBm (PrePowerAvg_FWD_BF);

				//PowerPrevio_BF_dBm = 10 * (log10(PrePowerAvg_FWD_BF) - 3/*=log10(1000)*/);
				//ArrayTControl[REG_POT1PREVIO_BF].s_16 = 10 * PowerPrevio_BF_dBm + 10/*dBm CORRECCIÓN EMPÍRICA!*/;
				DEBUG_PotPre_Fwd_BF = ArrayTControl[REG_POT1PREVIO_BF].s_16;
			}

			//Incident Power HPA1 BF (20Mhz - 500Mhz) - J1
			//HPA BF Temperature Sensor
			signed short Temp_C_BF = ADC12_Convert_V2T (TempeAvg_BF/*mV*/);
			ArrayTControl[REG_T_BF].s_16 = Temp_C_BF;
			//DEBUG_Temp_C_BF = ArrayTControl[REG_T_BF].s_16;

			if(PowerSum_FWD_BF != 0)
			{
				//Usando: Recta de ajuste P.Incidente BF SN-140001 250Mhz -> y = 0,0339x - 28,596 | Añadimos offset empírico de 1.5dBm
				//ArrayTControl[REG_POTFWD_BF].s_16 = 10 * ( (0.0339 * PowerAvg_FWD_BF) - 28.596 /*+ 1.5*/);	//In 10*dBm
				//ArrayTControl[REG_POTFWD_BF].s_16 = (unsigned long)((2777 * PowerAvg_FWD_BF) >> 13) - 286;
				//EDUARDO
				ArrayTControl[REG_POTFWD_BF].s_16 = (ADC12_Convert_uW2dBm (PowerAvg_FWD_BF) + 20) - (30/*Eduardo*/) /*---> Empiric Correction!!!*/;	//con pruebas exha +20
				//SUMAR --> NÚMERO EN PANTALLA ECR MÁS ALTO - MENOS POTENCIA REAL
				//RESTAR --> NUMERO PANTALLA ECR MÁS BAJO - MÁS POTENCIA REAL

				//PowerInc_BF_dBm = 10 * (log10(PowerAvg_FWD_BF) - 3/*=log10(1000)*/) ;	//to mW and then to dBm
				//ArrayTControl[REG_POTFWD_BF].s_16 = 10 * PowerInc_BF_dBm;
				DEBUG_Pot_Fwd_BF = ArrayTControl[REG_POTFWD_BF].s_16 	/*dBm x 10*/;
			}

			//Reflected Power HPA1 BF - J2
			if(PowerSum_RVS_BF != 0)
			{
				//Usando: Recta de ajuste P.Incidente BF SN-140001 250Mhz -> y = 0,0339x - 28,596
				//ArrayTControl[REG_POTRVS_BF].s_16 = 10 * ( (0.0339 * PowerAvg_RVS_BF) - 28.596 );	//In 10*dBm
				//ArrayTControl[REG_POTRVS_BF].s_16 = (unsigned long)((2777 * PowerAvg_RVS_BF) >> 13) - 286;

					ArrayTControl[REG_POTRVS_BF].s_16 = (ADC12_Convert_uW2dBm (PowerAvg_RVS_BF)) - (30/*Eduardo*/)/*---> Empiric Correction!!!*/;
				//PowerRef_BF_dBm = 10 * (log10(PowerAvg_RVS_BF) - 3/*=log10(1000)*/) ;		//to mW and then to dBm
				//ArrayTControl[REG_POTRVS_BF].s_16 = 10 * PowerRef_BF_dBm;
				DEBUG_Pot_Rvs_BF = ArrayTControl[REG_POTRVS_BF].s_16 /*dBm x 10*/;
			}

			short RL_BF = (ArrayTControl[REG_POTFWD_BF].s_16 - ArrayTControl[REG_POTRVS_BF].s_16); //(Return Loss * 10) in dB
			DEBUG_RL_BF = RL_BF;
			/*COMMENT LINE FOR DEBUGGING FSM SWR*/ ArrayTControl[REG_SWR_BF].s_16 = RL_BF;



			//VOLT PSU BF
			int Volt_DCDC_BF = ADC12_Convert_mV2V (VoltageAvg_BF/*mV*/);
			ArrayTControl[REG_TENSION_BF].s_16 = Volt_DCDC_BF;

			//Reset Sum and AVG values for Power and Temperature
			PowerSum_FWD_BF = 0;
			PowerSum_RVS_BF = 0;
			PrePowerSum_FWD_BF = 0;
			PowerAvg_FWD_BF = 0;
			PowerAvg_RVS_BF = 0;
			PrePowerAvg_FWD_BF = 0;
			TempeSum_BF = 0;
			TempeAvg_BF = 0;
			VoltageSum_BF = 0;
			VoltageAvg_BF = 0;
			Power_FWD_BF = 0;
			Power_RVS_BF = 0;
			PrePower_FWD_BF = 0;
			PowerInc_BF_dBm = 0;
			PowerRef_BF_dBm = 0;
			PowerPrevio_BF_dBm = 0;
			//--------------------------------------------------------------
		}


		if (CardType == AF)
		{
			//--------------------------------------------------------------
			PrePowerAvg_FWD_AF = PrePowerSum_FWD_AF / AD_SAMPLES_PRUEBA /*>> AD_SAMPLES_PRUEBA_EXP*/;		//En uW !
			PowerAvg_FWD_AF = PowerSum_FWD_AF / AD_SAMPLES_PRUEBA /*>> AD_SAMPLES_PRUEBA_EXP*/;				//En uW !
			PowerAvg_RVS_AF = PowerSum_RVS_AF / AD_SAMPLES_PRUEBA /*>> AD_SAMPLES_PRUEBA_EXP*/;				//En uW !

			TempeAvg_AF = TempeSum_AF >> 4;			//2^12 escalones del ADC12 y 2^6 del num de muestras
			TempeAvg_AF *= AD_VREF_PLUS_STEP_mV;
			TempeAvg_AF = TempeAvg_AF >> 14;

			VoltageAvg_AF = VoltageSum_AF >> 4;			//2^12 escalones del ADC12 y 2^6 del num de muestras
			VoltageAvg_AF *= AD_VREF_PLUS_STEP_mV;
			VoltageAvg_AF = VoltageAvg_AF >> 14;

			//Power Previo 2 AF (Previo AF)
			if(PrePowerSum_FWD_AF != 0)
			{
				//Usando recta de ajuste P.Previo AF SN-140001 2250Mhz -> y = 0,0315x - 23,379
				//ArrayTControl[REG_POT1PREVIO_AF].s_16 = 10 * ((0.0315 * PrePowerAvg_FWD_AF) - 23.379);		//In 10*dBm
				//ArrayTControl[REG_POT1PREVIO_AF].s_16 = (unsigned long)(((5611 * PrePowerAvg_FWD_AF) >> 4) - 239401) >> 10;

				ArrayTControl[REG_POT1PREVIO_AF].s_16 = ADC12_Convert_uW2dBm (PrePowerAvg_FWD_AF) + 00/*---> Empiric Correction!!!*/;

				//PowerPrevio_AF_dBm = 10 * (log10(PrePowerAvg_FWD_AF) - 3/*=log10(1000)*/);	//Combinar 2 lineas en una instrucción
				//ArrayTControl[REG_POT1PREVIO_AF].s_16 = 10 * PowerPrevio_AF_dBm;
				DEBUG_PotPre_Fwd_AF = ArrayTControl[REG_POT1PREVIO_AF].s_16;
			}

			//Incident Power HPA2 AF (0.5Ghz - 2.7Ghz) - J3
			if(PowerSum_FWD_AF != 0)
			{
				//Usando: Recta de ajuste P.Incidente AF SN-140001 1500Mhz -> y = 0,0333x - 26,441
				//ArrayTControl[REG_POTFWD_AF].s_16 = 10 * ((0.033 * PowerAvg_FWD_AF) - 26.441);		//In 10*dBm
				//ArrayTControl[REG_POTFWD_AF].s_16 = (unsigned long)( ((169 * PowerAvg_FWD_AF) >> 1) - 67689) >> 8;

				ArrayTControl[REG_POTFWD_AF].s_16 = (ADC12_Convert_uW2dBm (PowerAvg_FWD_AF) + 05) - (30/*Eduardo*/)  /*-15*/  /*---> Empiric Correction!!!*/;	//test exha -15
				//SUMAR --> NÚMERO EN PANTALLA ECR MÁS ALTO - MENOS POTENCIA REAL
				//RESTAR --> NUMERO PANTALLA ECR MÁS BAJO - MÁS POTENCIA REAL

				//PowerInc_AF_dBm = 10 * (log10(PowerAvg_FWD_AF) - 3/*=log10(1000)*/) ;		//to mW and then to dBm
				//ArrayTControl[REG_POTFWD_AF].s_16 = 10 * PowerInc_AF_dBm;
				DEBUG_Pot_Fwd_AF = ArrayTControl[REG_POTFWD_AF].s_16;
			}

			//Reflected Power HPA2 AF - J4
			if(PowerSum_RVS_AF != 0)
			{
				//Usando: Recta de ajuste P.Incidente AF SN-140001 1500Mhz -> y = 0,0333x - 26,441
				//ArrayTControl[REG_POTRVS_AF].s_16 = 10 * ((0.033 * PowerAvg_RVS_AF) - 26.441);		//In 10*dBm
				//ArrayTControl[REG_POTRVS_AF].s_16 = (unsigned long)( ((169 * PowerAvg_RVS_AF) >> 1) - 67689) >> 8;

				ArrayTControl[REG_POTRVS_AF].s_16 = (ADC12_Convert_uW2dBm (PowerAvg_RVS_AF)) - (30/*Eduardo*/);

				//PowerRef_AF_dBm = 10 * (log10(PowerAvg_RVS_AF) - 3/*=log10(1000)*/);		//to mW and then to dBm
				//ArrayTControl[REG_POTRVS_AF].s_16 = 10 * PowerRef_AF_dBm;
				DEBUG_Pot_Rvs_AF = ArrayTControl[REG_POTRVS_AF].s_16;
			}

			//SWR HPA2 AF
			short RL_AF = (ArrayTControl[REG_POTFWD_AF].s_16 - ArrayTControl[REG_POTRVS_AF].s_16); //(Return Loss * 10) in dB
			DEBUG_RL_AF = RL_AF;
			ArrayTControl[REG_SWR_AF].s_16 = RL_AF;

			//HPA AF Temperature Sensor
			signed short Temp_C_AF = ADC12_Convert_V2T (TempeAvg_AF/*mV*/);
			ArrayTControl[REG_T_AF].s_16 = Temp_C_AF;

			//VOLT PSU AF
			unsigned short Volt_DCDC_AF = ADC12_Convert_mV2V (VoltageAvg_AF/*mV*/);
			ArrayTControl[REG_TENSION_AF].s_16 = Volt_DCDC_AF;

			//Reset Sum and AVG values for Power and Temperature
			PowerSum_FWD_AF = 0;
			PowerSum_RVS_AF = 0;
			PrePowerSum_FWD_AF = 0;
			PowerAvg_FWD_AF = 0;
			PowerAvg_RVS_AF = 0;
			PrePowerAvg_FWD_AF = 0;
			TempeSum_AF = 0;
			TempeAvg_AF = 0;
			VoltageSum_AF = 0;
			VoltageAvg_AF = 0;
			Power_FWD_AF = 0;
			Power_RVS_AF = 0;
			PrePower_FWD_AF = 0;
			PowerInc_AF_dBm = 0;
			PowerRef_AF_dBm = 0;
			PowerPrevio_AF_dBm = 0;
			//--------------------------------------------------------------
		}

	//AUX_2_POUT &= ~AUX_2_MASK; //Toogle pin 9.5
	}


	//LED_1_POUT &= ~LED_1_MASK;			//Pin 9.2 (Sparta & Platinum)
}


unsigned short ADC12_Convert_V2T (unsigned short Temp_mV)
{
	signed short Temp1_C = 0;
	signed short Temp2_C = 0;
	//signed short Temp3_C = 0;

	//Formula taken from HPA BF
	Temp1_C = ((signed short)Temp_mV - 750) / 10;
	Temp2_C = 25/*ºC*/ + Temp1_C;

	//PATCH!!! DUE TO HPA AF TEMP MEASUREMENT PROBLEM!!
	//---- Taken out for Sparta ----
//	if (Temp2_C <= 25)	//Min measurable value returned by the HPAs
//		Temp2_C = 25;
//	if (Temp2_C >= 77)
//		Temp2_C = 77;
	//-----------------------------

	return Temp2_C; //ºC
}


unsigned short ADC12_Convert_mV2V (unsigned short mV_ADC12)
{
	unsigned long V_DCDC = 0;

	//V_DCDC = (0,0143 * mV_ADC12) + 0,0059;	//Esto es con R2=750
	//V_DCDC = (0,0157 * mV_ADC12) + 0,006;		//Esto es con R2=680	//EN SPARTA MONTA 680

	V_DCDC = ((unsigned long) mV_ADC12 * 157);
	V_DCDC = (V_DCDC/*+5000*/) / 1000/*00 dV*/;				//Truco: es como sumarle 1/2 para hacer un pseudo round ya que por defecto hace un floor

	return (unsigned short) V_DCDC;
}

